import React, { useState } from "react";
import MapComponent from "./MapComponent";
import DailyLogSheet from "./DailyLogSheet";
import TripForm from "./TripForm";
import { Container, Row, Col } from "react-bootstrap";

function App() {
  const [logEntries, setLogEntries] = useState([]);
  const [tripDetails, setTripDetails] = useState(null);

  const handleFormSubmit = async (tripData) => {
    try {
      // First, create the trip in the backend
      const response = await fetch("/api/trips/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(tripData),
      });

      if (!response.ok) {
        console.error("Error creating trip:", response.status);
        return;
      }
      const newTrip = await response.json();
      console.log("Created trip", newTrip);

      // Then, generate logs for the trip
      const logsResponse = await fetch(
        `/api/trips/${newTrip.id}/generate_logs/`,
        {
          method: "POST",
        }
      );

      if (!logsResponse.ok) {
        console.error("Error generating logs:", logsResponse.status);
        return;
      }

      // Finally, fetch the generated logs
      const logsDataResponse = await fetch(`/api/trips/${newTrip.id}/logs/`);
      if (!logsDataResponse.ok) {
        console.error("Error fetching logs:", logsDataResponse.status);
        return;
      }

      const logsData = await logsDataResponse.json();
      console.log("Logging the logs", logsData);

      setTripDetails(tripData);
      setLogEntries(logsData);
    } catch (error) {
      console.error("There was an issue", error);
    }
  };

  return (
    <Container>
      <h1>Trucker Logbook App</h1>
      <Row>
        <Col md={6}>
          <TripForm onSubmit={handleFormSubmit} />
        </Col>
        <Col md={6}>
          {tripDetails && (
            <MapComponent
              startLocation={tripDetails.startLocation}
              pickupLocation={tripDetails.pickupLocation}
              dropoffLocation={tripDetails.dropoffLocation}
            />
          )}
        </Col>
      </Row>
      <Row>
        <Col>
          {logEntries.length > 0 && <DailyLogSheet logEntries={logEntries} />}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
