import React, { useEffect } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css"; // Import Leaflet's CSS

const MapComponent = ({ startLocation, pickupLocation, dropoffLocation }) => {
  useEffect(() => {
    // Create the map
    const map = L.map("map", {
      center: [40.7128, -74.006], // Default center (e.g., New York City)
      zoom: 8,
      scrollWheelZoom: false,
    });

    // Add a tile layer (you can use a different tile provider if you prefer)
    L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
      attribution:
        '© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
    }).addTo(map);

    // Add markers for start, pickup, and dropoff locations (if provided)
    if (startLocation) {
      //TODO: Geocode startLocation and use those coordinates
      L.marker([40.7128, -74.006])
        .addTo(map)
        .bindPopup("Start Location: " + startLocation)
        .openPopup();
    }

    if (pickupLocation) {
      //TODO: Geocode pickupLocation and use those coordinates
      L.marker([41.8781, -87.6298])
        .addTo(map)
        .bindPopup("Pickup Location: " + pickupLocation)
        .openPopup();
    }

    if (dropoffLocation) {
      //TODO: Geocode dropoffLocation and use those coordinates
      L.marker([34.0522, -118.2437])
        .addTo(map)
        .bindPopup("Dropoff Location: " + dropoffLocation)
        .openPopup();
    }

    // Clean up the map when the component unmounts
    return () => {
      map.remove();
    };
  }, [startLocation, pickupLocation, dropoffLocation]);

  return <div id="map" style={{ height: "500px" }}></div>;
};

export default MapComponent;
