import React, { useState } from "react";
import {
  Form, Button, Container,
  ard
} from "react-bootstrap";
import moment from "moment";

const TripForm = ({ onSubmit }) => {
  const [startLocation, setStartLocation] = useState("");
  const [pickupLocation, setPickupLocation] = useState("");
  const [dropoffLocation, setDropoffLocation] = useState("");
  const [currentCycleHours, setCurrentCycleHours] = useState(0);
  const [startDate, setStartDate] = useState("");

  const handleSubmit = (event) => {
    event.preventDefault();
    const formattedDate = moment(startDate).format("YYYY-MM-DD");
    onSubmit({
      startLocation,
      pickupLocation,
      dropoffLocation,
      currentCycleHours,
      startDate: formattedDate,
    });
  };

  return (
    <div className="d-flex justify-content-center align-items-center vh-100">
      <Container>
        <Card
          className="p-4 shadow-lg"
          style={{ maxWidth: "500px", margin: "auto" }}
        >
          <Card.Body>
            <h2 className="text-center mb-4">Enter Trip Details</h2>
            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-3">
                <Form.Label>Start Location:</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter start location"
                  value={startLocation}
                  className="p-3"
                  onChange={(e) => setStartLocation(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Pickup Location:</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter pickup location"
                  value={pickupLocation}
                  className="p-3"
                  onChange={(e) => setPickupLocation(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Dropoff Location:</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Enter dropoff location"
                  value={dropoffLocation}
                  className="p-3"
                  onChange={(e) => setDropoffLocation(e.target.value)}
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Current Cycle Hours:</Form.Label>
                <Form.Control
                  type="number"
                  placeholder="Enter current cycle hours"
                  value={currentCycleHours}
                  className="p-3"
                  onChange={(e) =>
                    setCurrentCycleHours(parseFloat(e.target.value))
                  }
                />
              </Form.Group>

              <Form.Group className="mb-3">
                <Form.Label>Start Date:</Form.Label>
                <Form.Control
                  type="date"
                  placeholder="Enter start date"
                  value={startDate}
                  className="p-3"
                  onChange={(e) => setStartDate(e.target.value)}
                />
              </Form.Group>

              <Button variant="primary" type="submit" className="w-100 p-3">
                Generate Logs
              </Button>
            </Form>
          </Card.Body>
        </Card>
      </Container>
    </div>
  );
};

export default TripForm;
