import React, { useEffect, useRef } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";

const MapComponent = ({ startLocation, pickupLocation, dropoffLocation }) => {
  const mapRef = useRef(null); // Create a ref to hold the map container
  const mapInstance = useRef(null); // Use a ref to store the map instance

  useEffect(() => {
    const createMap = () => {
      if (mapRef.current && !mapInstance.current) {
        // Check if the map container exists and map instance is not yet created
        const map = L.map(mapRef.current, {
          center: [40.7128, -74.006],
          zoom: 8,
          scrollWheelZoom: false,
        });

        L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
          attribution:
            '© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        }).addTo(map);

        mapInstance.current = map; // Store the map instance
      }
    };

    createMap();

    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove();
      }
    };
  }, []); // Run only once on mount

  return <div id="map" ref={mapRef} style={{ height: "500px" }}></div>; // Attach the ref to the div
};

export default MapComponent;
