import React, { useEffect, useRef } from "react";
import L from "leaflet";
import "leaflet/dist/leaflet.css";
import "leaflet-routing-machine/dist/leaflet-routing-machine.css";
import "leaflet-routing-machine";
import "./MapComponent.css"; // Import the custom CSS

// Fix for Leaflet icon path (Method 1)
delete L.Icon.Default.prototype._getIconUrl;

L.Icon.Default.mergeOptions({
  iconRetinaUrl: require("leaflet/dist/images/marker-icon-2x.png"),
  iconUrl: require("leaflet/dist/images/marker-icon.png"),
  shadowUrl: require("leaflet/dist/images/marker-shadow.png"),
});

const MapComponent = ({
  startLocation,
  pickupLocation,
  dropoffLocation,
}) => {
  const mapRef = useRef(null); // Ref to the map container div
  const mapInstance = useRef(null); // Ref to store the leaflet map instance

  useEffect(() => {
    const createMap = async () => {
      if (mapRef.current && !mapInstance.current) {
        // Check if the map container exists AND the map hasn't been initialized yet
        const map = L.map(mapRef.current, {
          center: [40.7128, -74.006],
          zoom: 8,
          scrollWheelZoom: false,
        });

        L.tileLayer("https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png", {
          attribution:
            '© <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors',
        }).addTo(map);

        mapInstance.current = map; // Store the map instance in the ref
      }
    };

    createMap();

    return () => {
      if (mapInstance.current) {
        mapInstance.current.remove(); // Clean up the map on unmount
      }
    };
  }, []); // Run only once on mount

  useEffect(() => {
    const createRoutingControl = async () => {
      if (mapInstance.current) {
        // Geocoding function (replace with a real geocoding service)
        const geocode = async (location) => {
          console.warn(
            "Geocoding is not fully implemented -- using a dummy lat/lon value"
          );
          switch (location) {
            case "Miami, FL":
              return [25.7617, -80.1918]; // Miami coordinates
            case "Orlando, FL":
              return [28.5383, -81.3792]; // Orlando coordinates
            case "Jacksonville, FL":
              return [30.3322, -81.6557]; // Jacksonville coordinates
            default:
              return null;
          }
        };

        const startCoords = await geocode(startLocation);
        const pickupCoords = await geocode(pickupLocation);
        const dropoffCoords = await geocode(dropoffLocation);

        if (!startCoords || !pickupCoords || !dropoffCoords) {
          console.error("Could not geocode all locations");
          return;
        }

        L.Routing.control({
          waypoints: [
            L.latLng(startCoords),
            L.latLng(pickupCoords),
            L.latLng(dropoffCoords),
          ],
          routeWhileDragging: false,
          showAlternatives: false,
          serviceUrl: "http://router.project-osrm.org/route/v1",
        }).addTo(mapInstance.current);
      }
    };

    createRoutingControl();
  }, [mapInstance.current, startLocation, pickupLocation, dropoffLocation]); // Run when mapInstance is available and locations change

  return <div id="map" ref={mapRef} style={{ height: "500px" }}></div>;
};

export default MapComponent;
