import React, { useState } from "react";
import MapComponent from "./MapComponent";
import "./App.css";
import DailyLogSheet from "./DailyLogSheet";
import TripForm from "./TripForm";
import { Container, Row, Col } from "react-bootstrap";

function App() {
  const [logEntries, setLogEntries] = useState([]);
  const [tripDetails, setTripDetails] = useState(null);

  const handleFormSubmit = (tripData) => {
    // In a real application, you would send this data to your Django backend
    // to generate the log entries.  For this example, we'll just set some
    // dummy data.
    setTripDetails(tripData);
    setLogEntries([
      {
        timestamp: "2024-08-15T08:00:00",
        duty_status: "ON",
        location: "New York, NY",
        remarks: "Pre-trip inspection",
      },
      {
        timestamp: "2024-08-15T11:00:00",
        duty_status: "DR",
        location: "Harrisburg, PA",
        remarks: "Driving",
      },
      // Add more dummy log entries here
    ]);
  };

  return (
    <Container>
      <h1>Trucker Logbook App</h1>
      <Row>
        <Col md={6}>
          <TripForm onSubmit={handleFormSubmit} />
        </Col>
        <Col md={6}>
          {tripDetails && (
            <MapComponent
              startLocation={tripDetails.startLocation}
              pickupLocation={tripDetails.pickupLocation}
              dropoffLocation={tripDetails.dropoffLocation}
            />
          )}
        </Col>
      </Row>
      <Row>
        <Col>
          {logEntries.length > 0 && <DailyLogSheet logEntries={logEntries} />}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
